package nl.fontys.sebivenlo.dao;

import java.lang.annotation.ElementType;
import java.lang.annotation.Retention;
import java.lang.annotation.RetentionPolicy;
import java.lang.annotation.Target;

/**
 * For fields being generated by the backing store. If the field does not
 * qualify as the id field of the entity, use this annotation instead.
 *
 * Just adding the annotation will tag the id generated, i.e. typically using
 * some sequence. It is also applicable to fields that get their value at
 * database insertion time, like where the default value of a time stamp is
 * {@code now()}.
 *
 * @author Pieter van den Hombergh {@code pieter.van.den.hombergh@gmail.com}
 */
@Retention( RetentionPolicy.RUNTIME )
@Target( ElementType.FIELD )
public @interface Generated {
}
